== {{translation key="Tasks.WebHome.list_of_tasks" /}} ==
{{velocity}} #set($query = "from doc.object(TaskCode.TaskClass) as task where doc.fullName <> 'TaskCode.TaskTemplate' order by task.duedate") #set($results = $services.query.xwql($query).execute())
{{html wiki="true"}} #foreach($taskid in $results) #set($taskpageref = $services.model.resolveDocument($taskid)) #set($taskpage = $xwiki.getDocument($taskpageref)) #set($task = $taskpage.getObject('TaskCode.TaskClass')) #set($tasktitle = $task.getProperty('title').value) #set($taskduedate = $task.getProperty('duedate').value) #set($taskdependencies = $task.getProperty('dependencies').value) #set($taskdeptodo = []) #set($taskdepinprogress = []) #set($taskdepdone = []) #foreach($dtaskid in $taskdependencies) #set($dtaskpageref = $services.model.resolveDocument($dtaskid)) #set($dtaskpage = $xwiki.getDocument($dtaskpageref)) #set($dtask = $dtaskpage.getObject('TaskCode.TaskClass')) #set($dtaskstatus = $dtask.getProperty('status').value) #if($dtaskstatus == 'todo') #set($discard = $taskdeptodo.add($dtask)) #elseif($dtaskstatus == 'inprogress') #set($discard = $taskdepinprogress.add($dtask)) #elseif($dtaskstatus == 'done') #set($discard = $taskdepdone.add($dtask)) #else {{error}}Invalid task status. Contact administrator{{/error}} #end #end #set($taskassignee = $task.getProperty('assignee').value) #set($taskassignees = []) #foreach($a in $taskassignee.split(',')) #set($assigneepageref = $services.model.resolveDocument($a)) #set($assigneepage = $xwiki.getDocument($assigneepageref)) #set($assignee = $assigneepage.getObject('XWiki.XWikiUsers')) #set($discard = $taskassignees.add($assignee)) #end (% class="task-card" %) ((( (% class="task-card-subtasks" %) ((( #foreach($tdone in $taskdepdone) (% class="task-card-subtasks-status task-card-subtasks-status-done" %)((())) #end #foreach($tinprogress in $taskdepinprogress) (% class="task-card-subtasks-status task-card-subtasks-status-inprogress" %)((())) #end #foreach($ttodo in $taskdeptodo) (% class="task-card-subtasks-status task-card-subtasks-status-todo" %)((())) #end ))) (% class="task-card-title" %) ((( [[$tasktitle>>$taskid]] ))) (% class="task-card-duedate" %) ((( Due on $taskduedate ))) (% class="task-card-assignees" %) ((( #foreach($assignee in $taskassignees) (% class="task-card-assignee user-avatar-wrapper" %) ((( #smallUserAvatar($assignee.getName()) ))) #end ))) ))) #end {{/html}}
